import React from "react";
import "./contact.css";
import {MdOutlineEmail} from "react-icons/md"
import { useRef } from 'react';
import emailjs from 'emailjs-com';

const Contacts = () => {
  const form = useRef();

  const sendEmail = (e) => {
    e.preventDefault();

    emailjs.sendForm('service_9tbd5oc', 'template_hk41lqa', form.current, '1whEtRTB_9-u4nG9h')
      .then((result) => {
          console.log(result.text);
      }, (error) => {
          console.log(error.text);
      });
      e.target.reset()
  };
  return(
    <section id="contact">
      <h2>Get in Touch</h2>


      <div className="container contact__container">
        <div className="contact_options">
          <article className="contact__option">
            <MdOutlineEmail className="contact__option-icon"/>
            <h4>Email</h4>
            <h5>webdeveloper@aleatrengrouse.co.uk</h5>
            <a href="mailto:aleatrengrouse@gmail.com" target="_blank">Send a Message</a>
          </article>
        </div>
        {/*End of Contact Options*/}
        <form ref={form} onSubmit={sendEmail}>
          <input type="text" name="name"placeholder="Your Full Name"require />
          <input type="email"name="email" placeholder="Your Email" required/>
          <textarea name="message" rows="7" placeholder="Your Message" required></textarea>
          <button type="submit" className="btn btn-primary">Send Message</button>
        </form>

      </div>
    </section>
  )
};

export default Contacts;
